                return errormessage(get_phrase("message_content_error"));
            }

            $aaaaam = array( "edit" => $edit );
            addbreadcrumb(array( get_phrase("button_edit") . ": " . $News["title"] => TSUE_ADMINCP_URL . "?action=" . $TSUE["action"] . "&amp;do=" . $TSUE["do"] . "&amp;edit=" . $edit ));
        }
        else
        {
