        }

        if( isset($_POST["News"]) ) 
        {
            $News = $_POST["News"];
            if( !$News["title"] ) 
            {
                $formErrors[] = get_phrase("news_error_no_title");
            }

            if( !$News["content"] ) 
            {
                $formErrors[] = get_phrase("news_error_no_content");
            }

            if( !isset($formErrors) ) 
            {
                $buildQuery = array( "active" => $News["active"], "title" => $News["title"], "content" => $News["content"] );
