    $AllNews = $TSUE["TSUE_Database"]->query("SELECT * FROM tsue_news ORDER BY active DESC, date DESC");
    if( !$TSUE["TSUE_Database"]->num_rows($AllNews) ) 
    {
        $tableContents = errormessage(get_phrase("news_no_news_yet"));
