        {
            $aaaaee .= "\r\n\t\t\t<h5>" . $aaaadu . $phpErrorLogFile . "</h5>\r\n\t\t\t<div class=\"phpErrorLog\">\r\n\t\t\t\t" . nl2br(file_get_contents($aaaadu . $phpErrorLogFile)) . "\r\n\t\t\t</div>";
        }

