{
    return "<img src=\"" . $Image["src"] . "\" alt=\"" . $Image["alt"] . "\" title=\"" . $Image["title"] . "\" class=\"" . $Image["class"] . "\" id=\"" . $Image["id"] . "\" rel=\"" . $Image["rel"] . "\" />";
