                {
                    $muteMember["end_date"] = $end_date;
                }
                else
                {
                    $muteMember["end_date"] = 0;
                }

                $TSUE["TSUE_Database"]->replace("tsue_member_mutes", $muteMember);
                $BuildQuery = array( "muted" => $mutetype );
