        $tableContents .= aaaaag(array( array( "width" => "90%", "text" => $aaaakb . " <span class=\"smallPassiveText\">" . $mute_date . " -- " . $end_date . " -- " . $muted_by . " -- " . $notes . " -- " . $listMutes . "</span>" ), array( "width" => "10%", "text" => $aaaalr ) ));
