function findpagenumber($Query = "", $perPage = 0)
{
    global $TSUE;
    if( !$Query || !$perPage ) 
    {
        return 0;
    }

    $totalRows = $TSUE["TSUE_Database"]->row_count($Query);
    if( $totalRows <= $perPage ) 
    {
        return 0;
    }

    return ceil($totalRows / $perPage);
}

