function banip($ip)
{
    $ip = preg_replace("/\\.+\$/", "", $ip);
    if( !preg_match("/^\\d+(\\.\\d+){0,2}(\\.\\d+|\\.\\*)?\$/", $ip) ) 
    {
        return false;
    }

    if( substr($ip, -2) == ".*" ) 
    {
        $ip = substr($ip, 0, -2);
    }

    $aaaaof = array(  );
    $aaaaog = array(  );
    $ipParts = tsue_explode(".", $ip);
    foreach( $ipParts as $part ) 
    {
        if( $part < 0 || 255 < $part ) 
        {
            return false;
        }

        $aaaaof[] = $part;
        $aaaaog[] = $part;
    }
    while( count($aaaaof) < 4 ) 
    {
        $aaaaof[] = 0;
        $aaaaog[] = 255;
    }
    $aaaaoh = $ipParts[0];
    $startLong = sprintf("%u", ip2long(implode(".", $aaaaof)));
    $aaaaoi = sprintf("%u", ip2long(implode(".", $aaaaog)));
    if( count($ipParts) < 4 ) 
    {
        $aaaaoj = implode(".", $ipParts) . ".*";
    }
    else
    {
        $aaaaoj = $ip;
    }

    return array( $aaaaoj, $aaaaoh, $startLong, $aaaaoi );
}

