function generate_random_string($length = 7, $chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijkmnopqrstuvwxyz234567890")
{
    $chars_length = strlen($chars) - 1;
    $string = $chars[rand(0, $chars_length)];
    $i = 1;
    while( $i < $length ) 
    {
        $r = $chars[rand(0, $chars_length)];
        if( $r != $string[$i - 1] ) 
        {
            $string .= $r;
        }

        $i = strlen($string);
    }
    return $string;
}

