\n}\r\n.paginationNoMySQL li a:hover, .paginationNoMySQL li.active a\r\n{\r\n	background: url(\'styles/{themename}/theme_images/navigation.png\');\r\n	color: #fff;\r\n	border: 1px solid #ccc;\r\n	text-decoration: none;\r\n	text-shadow: #333333 1px 1px;\r\n}\r\n\r\n/***** Secondary Navigation ********************/\r\n#subnav\r\n{\r\n	width: 940px;\r\n	background: #F5F5F5;\r\n	margin: 0 auto 10px;\r\n	border: 1px solid #DDDDDD !important;\r\n	overflow: hidden;\r\n	clear: both;\r\n	-moz-box-shadow: 0 0 5px #ccc inset;-webkit-box-shadow: 0 0 5px #ccc inset;\r\n}\r\n\r\n#subnav ul\r\n{\r\n	width: 100%;\r\n	float: left;\r\n	list-style: none;\r\n	margin: 0;\r\n	padding: 0;\r\n}\r\n\r\n#subnav li\r\n{\r\n	float: left;\r\n	list-style: none;\r\n}\r\n\r\n#subnav li a\r\n{\r\n	color: #333333;\r\n	display: block;\r\n	font-family: Georgia, Arial, Tahoma, Verdana;\r\n	font-size: 14px;\r\n    font-weight: normal;\r\n    margin: 0;\r\n	padding: 7px 15px 7px 15px;\r\n	text-decoration: none;\r\n	position: relative;\r\n}\r\n\r\n#subnav li a:hover, #subnav li a:active, #subnav .current_page_item a, #subnav .current-menu-item a\r\n{\r\n	color: #e47600;\r\n}\r\n\r\n#subnav li a .sf-sub-indicator\r\n{\r\n	display: block;\r\n	width: 10px;\r\n	height: 10px;\r\n	top: 0;\r\n	right: 5px;\r\n	text-indent: -9999px;\r\n	overflow: hidden;\r\n	position: absolute;\r\n}\r\n\r\n#subnav li li a, #subnav li li a:link, #subnav li li a:visited\r\n{\r\n	background: #F5F5F5;\r\n	color: #333333;\r\n	width: 138px; \r\n	font-size: 13px;\r\n	margin: 0;\r\n	padding: 5px 10px 5px 10px;\r\n	border-left: 1px solid #DDDDDD;\r\n	border-right: 1px solid #DDDDDD;\r\n	border-bottom: 1px solid #DDDDDD;\r\n	text-shadow: #fff 1px 1px;\r\n	text-transform: none;\r\n	position: relative;\r\n}\r\n\r\n#subnav li li a:hover, #subnav li li a:active\r\n{\r\n	color: #e47600;\r\n}\r\n\r\n#subnav li li a .sf-sub-indicator\r\n{\r\n	background: url(images/arrow-right.png);\r\n	top: 10px;\r\n}\r\n\r\n#subnav li ul\r\n{\r\n	z-index: 9999;\