(3, 'reset.css', 1, '/*\r\nCopyright (c) 2010, Yahoo! Inc. All rights reserved.\r\nCode licensed under the BSD License:\r\nhttp://developer.yahoo.com/yui/license.html\r\nversion: 3.3.0\r\nbuild: 3167\r\n*/\r\nhtml{color:#000;background:#FFF;}body,div,dl,dt,dd,ul,ol,li,h1,h2,h3,h4,h5,h6,pre,code,form,fieldset,legend,input,textarea,p,blockquote,th,td{margin:0;padding:0;}table{border-collapse:collapse;border-spacing:0;}fieldset,img{border:0;}address,caption,cite,code,dfn,em,strong,th,var{font-style:normal;font-weight:normal;}li{list-style:none;}caption,th{text-align:left;}h1,h2,h3,h4,h5,h6{font-size:100%;font-weight:normal;}q:before,q:after{content:\'\';}abbr,acronym{border:0;font-variant:normal;}sup{vertical-align:text-top;}sub{vertical-align:text-bottom;}input,textarea,select{font-family:inherit;font-size:inherit;font-weight:inherit;}input,textarea,select{*font-size:100%;}legend{color:#000;}', 1404823543, ),
(4, 'jqueryTools.css', 1, '/* DateInput */\r\n.date\r\n{\r\n}\r\n\r\n/* calendar root element */\r\n#calroot\r\n{\r\n	/* place on top of other elements. set a higher value if nessessary */\r\n	z-index:100000;\r\n	margin-top:-1px;\r\n	width:198px;\r\n	padding:2px;\r\n	background-color:#fff;\r\n	font-size:11px;\r\n	border:1px solid #ccc;\r\n	-moz-border-radius:5px;\r\n	-webkit-border-radius:5px;\r\n	-moz-box-shadow: 0 0 15px #666;\r\n	-webkit-box-shadow: 0 0 15px #666;	\r\n}\r\n\r\n/* head. contains title, prev/next month controls and possible month/year selectors */\r\n#calhead\r\n{	\r\n	padding:2px 0;\r\n	height:22px;\r\n} \r\n\r\n#caltitle\r\n{\r\n	font-size:14px;\r\n	color:#0150D1;	\r\n	float:left;\r\n	text-align:center;\r\n	width:155px;\r\n	line-height:20px;\r\n	text-shadow:0 1px 0 #ddd;\r\n}\r\n\r\n#calnext, #calprev\r\n{\r\n	display:block;\r\n	width:20px;\r\n	height:20px;\r\n	background:transparent url(\'styles/{themename}/jqueryTools/prev.gif\') no-repeat scroll center center;\r\n	float:left;\r\n	cursor:pointer;\r\n}\r\n\r\n#calnext\r\n{\r\n	background-image:url(\'styles/{themename}/jqueryTools/next.gif\');\r\n	float:right;\r\n}\r\n\r\n#calprev.caldisabled, #calnext.caldisabled\r\n{\r\n	visibility:hidden;	\r\n}\r\n\r\n/* year/month selector */\r\n#caltitle select\r\n{\r\n	font-size:10px;	\r\n}\r\n\r\n/* names of the days */\r\n#caldays\r\n{\r\n	height:14px;\r\n	border-bottom:1px solid #ddd;\r\n}\r\n\r\n#caldays span\r\n{\r\n	display:block;\r\n	float:left;\r\n	width:28px;\r\n	text-align:center;\r\n}\r\n\r\n/* container for weeks */\r\n#calweeks\r\n{\r\n	background-color:#fff;\r\n	margin-top:4px;\r\n}\r\n\r\n/* single week */\r\n.calweek\r\n{\r\n	clear:left;\r\n	height:22px;\r\n}\r\n\r\n/* single day */\r\n.calweek a\r\n{\r\n	display:block;\r\n	float:left;\r\n	width:27px;\r\n	height:20px;\r\n	text-decoration:none;\r\n	font-size:11px;\r\n	margin-left:1px;\r\n	text-align:center;\r\n	line-height:20px;\r\n	color:#666;\r\n	-moz-border-radius:3px;\r\n	-webkit-border-radius:3px; 		\r\n} \r\n\r\n/* different states */\r\n.calweek a:hover, .calfocus\r\n{\r\n	background-color:#ddd;\r\n}\r\n\r\n/* sunday */\r\na.calsun\r\n{\r\n	color:red;		\r\n}\r\n\r\n/* offmonth day */\r\na.caloff\r\n{\r\n	color:#ccc;		\r\n}\r\n\r\na.caloff:hover\r\n{\r\n	background-color:rgb(245, 245, 250);		\r\n}\r\n\r\n/* unselecteble day */\r\na.caldisabled\r\n{\r\n	background-color:#efefef !important;\r\n	color:#ccc	!important;\r\n	cursor:default;\r\n}\r\n\r\n/* current day */\r\n#calcurrent\r\n{\r\n	background-color:#498CE2;\r\n	color:#fff;\r\n}\r\n\r\n/* today */\r\n#caltoday\r\n{\r\n	background-color:#333;\r\n	color:#fff;\r\n}\r\n/* DateInput */\r\n\r\n\r\n/* SCROLLABLE */\r\n/*\r\n	root element for the scrollable.\r\n	when scrolling occurs this element stays still.\r\n*/\r\n.scrollable \r\n{\r\n	/* required settings */\r\n	position:relative;\r\n	overflow:hidden;\r\n	width: 99.8%;\r\n	height: 180px;\r\n	/* custom decorations */\r\n	border:1px solid #ccc;\r\n	background:url(\'styles/{themename}/jqueryTools/h300.png\') repeat-x;\r\n	margin: 0 auto 10px auto;\r\n}\r\n\r\n.vertical\r\n{\r\n	width: 99%;\r\n}\r\n\r\n/*\r\n	root element for scrollable items. Must be absolutely positioned\r\n	and it should have a extremely large width to accomodate scrollable items.\r\n	it\'s enough that you set the width and height for the root element and\r\n	not for this element.\r\n*/\r\n.scrollable .items\r\n{\r\n	/* this cannot be too large */\r\n	width:20000em;\r\n	position:absolute;\r\n	clear:both;\r\n}\r\n\r\n.scrollable .items div.widthSidebar\r\n{\r\n	float:left;\r\n	width: 663px;\r\n}\r\n.scrollable .items div.widthoutSidebar\r\n{\r\n	float:left;\r\n	width: 938px;\r\n}\r\n\r\n/* single scrollable item */\r\n.scrollable img\r\n{\r\n	float:left;\r\n	margin: 10px 11px 10px 10px;\r\n	background-color:#fff;\r\n	padding:2px;\r\n	border:1px solid #ccc;\r\n	width: 105px;\r\n	height: 155px;\r\n	-moz-border-radius:4px;\r\n	-webkit-border-radius:4px;\r\n}\r\n\r\n.vertical img\r\n{\r\n	margin: 10px 6px;\r\n}\r\n\r\n/* active item */\r\n.scrollable .active\r\n{\r\n	border:2px solid #000;\r\n	position:relative;\r\n	cursor:default;\r\n}\r\n\r\n/* this makes it possible to add next button beside scrollable */\r\n.scrollable\r\n{\r\n	float:left;	\r\n}\r\n\r\n/* prev, next, prevPage and nextPage buttons */\r\na.browse\r\n{\r\n	background:url(\'styles/{themename}/jqueryTools/hori_large.png\') no-repeat;\r\n	display:none;\r\n	width:30px;\r\n	height:30px;\r\n	float:left;\r\n	margin:40px 10px;\r\n	cursor:pointer;\r\n	font-size:1px;\r\n}\r\n\r\n/* right */\r\na.right 				{ background-position: 0 -30px; clear:right; margin-right: 0px;}\r\na.right:hover 		{ background-position:-30px -30px; }\r\na.right:active 	{ background-position:-60px -30px; } \r\n\r\n\r\n/* left */\r\na.left				{ margin-left: 0px; } \r\na.left:hover  		{ background-position:-30px 0; }\r\na.left:active  	{ background-position:-60px 0; }\r\n\r\n/* up and down */\r\na.up, a.down\r\n{ \r\n	background:url(\'styles/{themename}/jqueryTools/vert_large.png\') no-repeat; \r\n	float: none;\r\n	margin: 10px 50px;\r\n}\r\n\r\n/* up */\r\na.up:hover  		{ background-position:-30px 0; }\r\na.up:active  		{ background-position:-60px 0; }\r\n\r\n/* down */\r\na.down 				{ background-position: 0 -30px; }\r\na.down:hover  		{ background-position:-30px -30px; }\r\na.down:active  	{ background-position:-60px -30px; } \r\n\r\n\r\n/* disabled navigational button */\r\na.disabled\r\n{\r\n	visibility:hidden !important;		\r\n}\r\n/* SCROLLABLE */\r\n\r\n/*TABS*/\r\n.panes div, .tabItems\r\n{\r\n	display:none;		\r\n	padding:15px 10px;\r\n	border:1px solid #cccccc;\r\n	border-top:0;\r\n	min-height:100px;\r\n	background-color:#f7f7f7;\r\n}\r\n\r\n/* root element for tabs  */\r\nul.tabs\r\n{ \r\n	margin:0 !important; \r\n	padding:0;\r\n	height:30px;\r\n	border-bottom:1px solid #cccccc;\r\n}\r\n\r\n/* single tab */\r\nul.tabs li\r\n{ \r\n	float:left;	 \r\n	padding:0; \r\n	margin:0;  \r\n	list-style-type:none;\r\n}\r\n\r\n/* link inside the tab. uses a background image */\r\nul.tabs a\r\n{ \r\n	float:left;\r\n	display:block;\r\n	padding:5px 15px;	\r\n	text-decoration:none;\r\n	border-right:1px solid #ccc;	\r\n	border-top: 1px solid #ccc;\r\n	border-bottom:0px;\r\n	height:18px;\r\n	background-color:#373737;\r\n	color:#ffffff;\r\n	position:relative;\r\n	top:1px;	\r\n	outline:0;\r\n	-moz-border-radius:4px 4px 0 0;\r\n}\r\n\r\nul.tabs a:active\r\n{\r\n	outline:none;		\r\n}\r\n\r\n/* when mouse enters the tab move the background image */\r\nul.tabs a:hover\r\n{\r\n	background: #f0881a;\r\n	color:#fff;	\r\n}\r\n\r\n/* active tab uses a class name \"current\". its highlight is also done by moving the background image. */\r\nul.tabs a.current, ul.tabs a.current:hover, ul.tabs li.current a\r\n{\r\n	background: #d8d1d1;		\r\n	cursor:default !important; \r\n	color:#000 !important;\r\n	border-bottom:1px solid #cccccc;\r\n}\r\n\r\n\r\n/* initially all panes are hidden */ \r\n.panes .pane\r\n{\r\n	display:none;		\r\n}\r\n/*TABS*/', 1404823543, );
