(6, 'fileuploader.css', 1, '/*\r\n * Original version: 1.0 ? 2010 Andrew Valums ( andrew(at)valums.com )\r\n * Current Maintainer (2.0+): 2012, Ray Nicholus ( fineuploader(at)garstasio.com )\r\n *\r\n * Licensed under MIT license, GNU GPL 2 or later, GNU LGPL 2 or later, see license.txt.\r\n */\r\n\r\n #upload_a_file,#upload_a_file_overlay,#upload_screenshot{float: right; text-align: right; margin-left: 6px;}\r\n\r\n.qq-uploader {\r\n    position: relative;\r\n    width: 100%;\r\n}\r\n.qq-upload-button {\r\n   display:block; /* or inline-block */\r\n	text-align:center;    \r\n	background: url(\'styles/{themename}/theme_images/navigation.png\') center;\r\n	color: #FFFFFF;\r\n	font-size: 14px;\r\n	font-weight: normal;\r\n	padding: 3px 5px 2px 5px;\r\n	text-shadow: #333333 1px 1px;\r\n	-moz-border-radius: 2px;\r\n	-khtml-border-radius: 2px;\r\n	-webkit-border-radius: 2px;\r\n	border: 1px solid #333333;\r\n	cursor: pointer;\r\n}\r\n.qq-upload-button-hover {background: url(\'styles/{themename}/theme_images/navigation-hover.png\') center;text-decoration: none;border: 1px solid #E47600;}\r\n.qq-upload-button-focus {\r\n    outline: 1px dotted #000000;\r\n}\r\n.qq-upload-drop-area, .qq-upload-extra-drop-area {\r\n    position: absolute;\r\n    top: 0;\r\n    left: 0;\r\n    width: 100%;\r\n    height: 100%;\r\n    min-height: 30px;\r\n    z-index: 2;\r\n    background: #FF9797;\r\n    text-align: center;\r\n}\r\n.qq-upload-drop-area span {\r\n    display: block;\r\n    position: absolute;\r\n    top: 50%;\r\n    width: 100%;\r\n    margin-top: -8px;\r\n    font-size: 16px;\r\n}\r\n.qq-upload-extra-drop-area {\r\n    position: relative;\r\n    margin-top: 50px;\r\n    font-size: 16px;\r\n    padding-top: 30px;\r\n    height: 20px;\r\n    min-height: 40px;\r\n}\r\n.qq-upload-drop-area-active {\r\n    background: #FF7171;\r\n}\r\n.qq-upload-list {\r\n    margin: 0;\r\n    padding: 0;\r\n    list-style: none;\r\n}\r\n.qq-upload-list li {\r\n    margin: 0;\r\n    padding: 9px;\r\n    line-height: 11px;\r\n    font-size: 11px;\r\n    background-color: #FFF0BD;\r\n}\r\n.qq-upload-file, .qq-upload-spinner, .qq-upload-size, .qq-upload-cancel, .qq-upload-retry, .qq-upload-failed-text, .qq-upload-finished {\r\n    margin-right: 12px;\r\n}\r\n.qq-upload-file {\r\n}\r\n.qq-upload-spinner {\r\n    display: inline-block;\r\n    background: url(\"styles/{themename}/fileuploader/loading.gif\");\r\n    width: 15px;\r\n    height: 15px;\r\n    vertical-align: text-bottom;\r\n}\r\n.qq-drop-processing {\r\n    display: none;\r\n}\r\n.qq-drop-processing-spinner {\r\n    display: inline-block;\r\n    background: url(\"styles/{themename}/fileuploader/processing.gif\");\r\n    width: 24px;\r\n    height: 24px;\r\n    vertical-align: text-bottom;\r\n}\r\n.qq-upload-finished {\r\n    display:none;\r\n    width:15px;\r\n    height:15px;\r\n    vertical-align:text-bottom;\r\n}\r\n.qq-upload-retry {\r\n    display: none;\r\n    color: #000000;\r\n}\r\n.qq-upload-cancel {\r\n	color: #000000;\r\n}\r\n.qq-upload-retryable .qq-upload-retry {\r\n    display: inline;\r\n}\r\n.qq-upload-size, .qq-upload-cancel, .qq-upload-retry {\r\n    font-size: 12px;\r\n    font-weight: normal;\r\n}\r\n.qq-upload-failed-text {\r\n    display: none;\r\n    font-style: italic;\r\n    font-weight: bold;\r\n}\r\n.qq-upload-failed-icon {\r\n    display:none;\r\n    width:15px;\r\n    height:15px;\r\n    vertical-align:text-bottom;\r\n}\r\n.qq-upload-fail .qq-upload-failed-text {\r\n    display: inline;\r\n}\r\n.qq-upload-retrying .qq-upload-failed-text {\r\n    display: inline;\r\n    color: #D60000;\r\n}\r\n.qq-upload-list li.qq-upload-success {\r\n    background-color: #5DA30C;\r\n    color: #FFFFFF;\r\n}\r\n.qq-upload-list li.qq-upload-fail {\r\n    background-color: #D60000;\r\n    color: #FFFFFF;\r\n}\r\n.qq-progress-bar {\r\n    background: -moz-linear-gradient(top,  rgba(30,87,153,1) 0%, rgba(41,137,216,1) 50%, rgba(32,124,202,1) 51%, rgba(125,185,232,1) 100%); /* FF3.6+ */\r\n    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(30,87,153,1)), color-stop(50%,rgba(41,137,216,1)), color-stop(51%,rgba(32,124,202,1)), color-stop(100%,rgba(125,185,232,1))); /* Chrome,Safari4+ */\r\n    background: -webkit-linear-gradient(top,  rgba(30,87,153,1) 0%,rgba(41,137,216,1) 50%,rgba(32,124,202,1) 51%,rgba(125,185,232,1) 100%); /* Chrome10+,Safari5.1+ */\r\n    background: -o-linear-gradient(top,  rgba(30,87,153,1) 0%,rgba(41,137,216,1) 50%,rgba(32,124,202,1) 51%,rgba(125,185,232,1) 100%); /* Opera 11.10+ */\r\n    background: -ms-linear-gradient(top,  rgba(30,87,153,1) 0%,rgba(41,137,216,1) 50%,rgba(32,124,202,1) 51%,rgba(125,185,232,1) 100%); /* IE10+ */\r\n    background: linear-gradient(to bottom,  rgba(30,87,153,1) 0%,rgba(41,137,216,1) 50%,rgba(32,124,202,1) 51%,rgba(125,185,232,1) 100%); /* W3C */\r\n    width: 0%;\r\n    height: 15px;\r\n    border-radius: 6px;\r\n    margin-bottom: 3px;\r\n    display: none;\r\n}', 1404823543, );
