(7, 'tinymce_ui.css', 1, '@CHARSET \"UTF-8\";\r\n\r\n/* Reset */\r\n.TSUESkin table, .TSUESkin tbody, .TSUESkin a, .TSUESkin img, .TSUESkin tr, .TSUESkin div, .TSUESkin td, .TSUESkin iframe, .TSUESkin span, .TSUESkin *, .TSUESkin .mceText {border:0; margin:0; padding:0; background:transparent; white-space:nowrap; text-decoration:none; font-weight:normal; cursor:default; color:#000; vertical-align:baseline; width:auto; border-collapse:separate; text-align:left}\r\n.TSUESkin a:hover, .TSUESkin a:link, .TSUESkin a:visited, .TSUESkin a:active {text-decoration:none; font-weight:normal; cursor:default; color:#000}\r\n.TSUESkin table td {vertical-align:middle}\r\n\r\n/* Containers */\r\n.TSUESkin table {background:#F0F0EE}\r\n.TSUESkin iframe {display:block; background:#FFF}\r\n.TSUESkin .mceToolbar {height:26px}\r\n.TSUESkin .mceLeft {text-align:left}\r\n.TSUESkin .mceRight {text-align:right}\r\n\r\n/* External */\r\n.TSUESkin .mceExternalToolbar {position:absolute; border:1px solid #CCC; border-bottom:0; display:none;}\r\n.TSUESkin .mceExternalToolbar td.mceToolbar {padding-right:13px;}\r\n.TSUESkin .mceExternalClose {position:absolute; top:3px; right:3px; width:7px; height:7px; background:url(\'styles/{themename}/editor/icons.png\') -820px 0}\r\n\r\n/* Layout */\r\n.TSUESkin table.mceLayout {border:0; border-left:1px solid #CCC; border-right:1px solid #CCC}\r\n.TSUESkin table.mceLayout tr.mceFirst td {border-top:1px solid #CCC}\r\n.TSUESkin table.mceLayout tr.mceLast td {border-bottom:1px solid #CCC}\r\n.TSUESkin table.mceToolbar, .TSUESkin tr.mceFirst .mceToolbar tr td, .TSUESkin tr.mceLast .mceToolbar tr td {border:0; margin:0; padding:0;}\r\n.TSUESkin td.mceToolbar {padding-top:1px; vertical-align:top}\r\n.TSUESkin .mceIframeContainer {border-top:1px solid #CCC; border-bottom:1px solid #CCC}\r\n.TSUESkin .mceStatusbar {font-family: \'Trebuchet MS\', Helvetica, Arial, sans-serif; font-size:9pt; line-height:16px; overflow:visible; color:#000; display:block; height:20px}\r\n.TSUESkin .mceStatusbar div {float:left; margin:2px}\r\n.TSUESkin .mceStatusbar a.mceResize {display:block; float:right; background:url(\'styles/{themename}/editor/icons.png\') -800px 0; width:20px; height:20px; cursor:se-resize}\r\n.TSUESkin .mceStatusbar a:hover {text-decoration:underline}\r\n.TSUESkin table.mceToolbar {margin-left:3px}\r\n.TSUESkin span.mceIcon, .TSUESkin img.mceIcon {display:block; width:20px; height:20px}\r\n.TSUESkin .mceIcon {background:url(\'styles/{themename}/editor/icons.png\') no-repeat 20px 20px}\r\n.TSUESkin td.mceCenter {text-align:center;}\r\n.TSUESkin td.mceCenter table {margin:0 auto; text-align:left;}\r\n.TSUESkin td.mceRight table {margin:0 0 0 auto;}\r\n\r\n/* Button */\r\n.TSUESkin .mceButton {display:block; border:1px solid #F0F0EE; width:20px; height:20px; margin-right:1px}\r\n.TSUESkin a.mceButtonEnabled:hover {border:1px solid #0A246A; background-color:#B2BBD0}\r\n.TSUESkin a.mceButtonActive, .TSUESkin a.mceButtonSelected {border:1px solid #0A246A; background-color:#C2CBE0}\r\n.TSUESkin .mceButtonDisabled .mceIcon {opacity:0.3; -ms-filter:\'alpha(opacity=30)\'; filter:alpha(opacity=30)}\r\n.TSUESkin .mceButtonLabeled {width:auto}\r\n.TSUESkin .mceButtonLabeled span.mceIcon {float:left}\r\n.TSUESkin span.mceButtonLabel {display:block; font-size:10px; padding:4px 6px 0 22px; font-family: \'Trebuchet MS\', Helvetica, Arial, sans-serif;}\r\n.TSUESkin .mceButtonDisabled .mceButtonLabel {color:#888}\r\n\r\n/* Separator */\r\n.TSUESkin .mceSeparator {display:block; background:url(\'styles/{themename}/editor/icons.png\') -180px 0; width:2px; height:20px; margin:2px 2px 0 4px}\r\n\r\n/* ListBox */\r\n.TSUESkin .mceListBox {direction:ltr}\r\n.TSUESkin .mceListBox, .TSUESkin .mceListBox a {display:block}\r\n.TSUESkin .mceListBox .mceText {padding-left:4px; width:70px; text-align:left; border:1px solid #CCC; border-right:0; background:#FFF; font-family: \'Trebuchet MS\', Helvetica, Arial, sans-serif; font-size:11px; height:20px; line-height:20px; overflow:hidden}\r\n.TSUESkin .mceListBox .mceOpen {width:9px; height:20px; background:url(\'styles/{themename}/editor/icons.png\') -741px 0; margin-right:2px; border:1px solid #CCC;}\r\n.TSUESkin table.mceListBoxEnabled:hover .mceText, .TSUESkin .mceListBoxHover .mceText, .TSUESkin .mceListBoxSelected .mceText {border:1px solid #A2ABC0; border-right:0; background:#FFF}\r\n.TSUESkin table.mceListBoxEnabled:hover .mceOpen, .TSUESkin .mceListBoxHover .mceOpen, .TSUESkin .mceListBoxSelected .mceOpen {background-color:#FFF; border:1px solid #A2ABC0}\r\n.TSUESkin .mceListBoxDisabled a.mceText {color:gray; background-color:transparent;}\r\n.TSUESkin .mceListBoxMenu {overflow:auto; overflow-x:hidden}\r\n.TSUESkin .mceOldBoxModel .mceListBox .mceText {height:22px}\r\n.TSUESkin .mceOldBoxModel .mceListBox .mceOpen {width:11px; height:22px;}\r\n.TSUESkin select.mceNativeListBox {font-family: \'Trebuchet MS\', Helvetica, Arial, sans-serif; font-size:7pt; background:#F0F0EE; border:1px solid gray; margin-right:2px;}\r\n\r\n/* SplitButton */\r\n.TSUESkin .mceSplitButton {width:32px; height:20px; direction:ltr}\r\n.TSUESkin .mceSplitButton a, .TSUESkin .mceSplitButton span {height:20px; display:block}\r\n.TSUESkin .mceSplitButton a.mceAction {width:20px; border:1px solid #F0F0EE; border-right:0;}\r\n.TSUESkin .mceSplitButton span.mceAction {width:20px; background:url(\'styles/{themename}/editor/icons.png\') 20px 20px;}\r\n.TSUESkin .mceSplitButton a.mceOpen {width:9px; background:url(\'styles/{themename}/editor/icons.png\') -741px 0; border:1px solid #F0F0EE;}\r\n.TSUESkin .mceSplitButton span.mceOpen {display:none}\r\n.TSUESkin table.mceSplitButtonEnabled:hover a.mceAction, .TSUESkin .mceSplitButtonHover a.mceAction, .TSUESkin .mceSplitButtonSelected a.mceAction {border:1px solid #0A246A; border-right:0; background-color:#B2BBD0}\r\n.TSUESkin table.mceSplitButtonEnabled:hover a.mceOpen, .TSUESkin .mceSplitButtonHover a.mceOpen, .TSUESkin .mceSplitButtonSelected a