(8, 'tinymce_content.css', 1, '@CHARSET \"UTF-8\";\r\n\r\nbody {background:#FFF;}\r\nbody.mceForceColors {background:#FFF; color:#000;}\r\nimg {border:0;}\r\np { margin: 0; padding: 0 }\r\nimg:-moz-broken {-moz-force-broken-image-icon:1; width:24px; height:24px}\r\nfont[face=mceinline] {font-family:inherit !important}\r\n\r\n/********* CODE/PHP BLOCKS  *********/\r\n.CodeBlock\r\n{\r\n	border: 1px solid #d7edfc;\r\n	border-radius: 5px; -webkit-border-radius: 5px; -moz-border-radius: 5px; -khtml-border-radius: 5px;\r\n}\r\n.CodeBlock pre\r\n{\r\n	margin: 0;\r\n}\r\n.CodeBlock img\r\n{\r\n	border: none;\r\n}\r\n.CodeBlock .title\r\n{\r\n	font-size: 11px;\r\n	color: #6cb2e4;\r\n	background: #d7edfc repeat-x top;\r\n	padding: 5px 10px;\r\n	border-bottom: 1px solid #a5cae4;\r\n	border-top-left-radius: 4px; -webkit-border-top-left-radius: 4px; -moz-border-radius-topleft: 4px; -khtml-border-top-left-radius: 4px;\r\n	border-top-right-radius: 4px; -webkit-border-top-right-radius: 4px; -moz-border-radius-topright: 4px; -khtml-border-top-right-radius: 4px;\r\n}\r\n.CodeBlock .title #expand_button\r\n{\r\n	float: right;\r\n}\r\n.CodeBlock pre\r\n{\r\n	font-size: 10pt;\r\n	font-family: Consolas, \'Courier New\', Courier, monospace;\r\n	background: #f0f7fc repeat-x top;\r\n	padding: 10px;\r\n	border-radius: 5px; -webkit-border-radius: 5px; -moz-border-radius: 5px; -khtml-border-radius: 5px;\r\n	word-wrap: normal;\r\n	overflow: auto;\r\n	line-height: 1.5;\r\n	min-height: 25px;\r\n	max-height: 300px;\r\n}\r\n\r\n/********* QUOTE BLOCKS ***********/\r\n.CodeBlockQuote\r\n{\r\n	border: 1px solid #f9d9b0;\r\n	border-radius: 5px; -webkit-border-radius: 5px; -moz-border-radius: 5px; -khtml-border-radius: 5px;\r\n}\r\n.CodeBlockQuote .title\r\n{\r\n	font-size: 11px;\r\n	color: #141414;\r\n	background: #f9d9b0 repeat-x top;\r\n	padding: 5px 10px;\r\n	border-bottom: 1px solid #f9d9b0;\r\n	border-top-left-radius: 4px; -webkit-border-top-left-radius: 4px; -moz-border-radius-topleft: 4px; -kh