(17, 'staffDropDownMenu.css', 1, '/* General dropdown styles */\r\ndl.dropdown{margin-right: 30px; display: inline-block; vertical-align: middle;}\r\n.threadTools dl.dropdown{margin: 0; margin-right: 10px;}\r\n.dropdown dd, .dropdown dt, .dropdown ul { margin:0px; padding:0px; margin-left:5px; }\r\n.dropdown dd { position:relative; }\r\n\r\n/* DT styles for sliding doors */\r\n.dropdown dt a {background: url(\'styles/{themename}/buttons/manageAccount.png\') no-repeat scroll left center;display:block; width:24px; height:24px; cursor:pointer;}\r\n\r\n/* UL styles */\r\n.dropdown dd ul { z-index: 9000; background:#EEEEEE none repeat scroll 0 0; display:none;list-style:none; padding:3px 0; position:absolute;right:5px; width:160px; right:auto; left:0; border:1px solid #656565; cursor:pointer;}\r\n.dropdown dd ul li{ background-color:#EEEEEE; margin:0; width:160px;}\r\n.dropdown dd ul li a { display:block; font-size: 11px; font-weight:normal; width:137px; text-align:left; padding:2px 4px 3px 19px; color:#111111; text-decoration:none;}\r\n.dropdown dd ul li a:hover{ background:#656565; color:white; text-decoration:none; }\r\n.dropdown dd ul li a:visited{ text-decoration:none; }', 1404823543, ),
