(61, 'sls.css', 1, '/* HTML & BODY */\r\nhtml\r\n{\r\n	padding: 0;\r\n	margin: 0;\r\n	color: #000;\r\n	background: #eee;\r\n}\r\n\r\nbody\r\n{\r\n	color: #333;\r\n	font-size: 13px;\r\n	font-family: \'Trebuchet MS\', Helvetica, Arial, sans-serif;\r\n	margin: 0 auto 0;\r\n	padding: 0;\r\n	line-height: 22px;\r\n}\r\n\r\n/* SLS */\r\n#sls\r\n{\r\n	position: relative;\r\n	margin: 0 auto;\r\n	padding: 20px 25px;\r\n	border: 1px solid #ccc;\r\n\r\n	background: url(\'styles/{themename}/theme_images/sls_wrap.jpg\') top center no-repeat;\r\n\r\n	width: 631px;\r\n\r\n	-moz-border-radius: 10px;\r\n	-webkit-border-radius: 10px;\r\n	border-radius: 10px; /* future proofing */\r\n	-khtml-border-radius: 10px; /* for old Konqueror browsers */\r\n\r\n	-moz-box-shadow: 0 0 5px #888;\r\n	-webkit-box-shadow: 0 0 5px#888;\r\n	box-shadow: 0 0 5px #888;\r\n}\r\n\r\n#logo\r\n{\r\n	width: 665px;\r\n	height: 65px;\r\n	margin: 200px auto 0 auto;\r\n	padding: 0;\r\n	background: url(\'styles/{themename}/theme_images/logo.png\') no-repeat;\r\n}\r\n\r\n#login\r\n{\r\n	float: left;\r\n}\r\n\r\n#ballonTooltip\r\n{\r\n	position: absolute;\r\n	right: 16px;\r\n	top: -6px;\r\n	background: url(\'styles/{themename}/tipsy/tooltipBallon.png\') no-repeat;\r\n	width: 170px;\r\n	height: 56px;\r\n	text-align: center;\r\n	line-height: 3.5;\r\n	color: #fff;\r\n	display: none;\r\n}\r\n\r\n.links\r\n{\r\n	margin-left: 5px;\r\n	margin-top: 5px;\r\n}\r\n\r\n/* MISC */\r\n.clear\r\n{\r\n	clear: both;\r\n}\r\n\r\n.middle\r\n{\r\n	border: 0;\r\n	vertical-align: middle;\r\n}\r\n\r\nlabel\r\n{\r\n	font-weight: bold;\r\n}\r\n\r\n/* Input Boxes */\r\n.inputDiv\r\n{\r\n	margin: 5px;\r\n	float: left;\r\n}\r\n\r\n.s \r\n{\r\n	background: #fff;\r\n	color: #333333 !important;\r\n	font-size: 12px !important;\r\n	padding: 5px !important;\r\n	border: 1px solid #E6E6E6 !important;\r\n	width: 250px;\r\n	-moz-border-radius: 0 !important;\r\n}\r\n.s:hover, #sidebar .s:hover \r\n{\r\n	background: #ffe;\r\n	border: 2px solid #000;\r\n}\r\n\r\n.submit\r\n{\r\n	background: url(\'styles/{themename}/theme_images/simple.header.png\') center;\r\n	color: #fff;\r\n	font-size: 14px;\r\n	font-weight: normal;\r\n	padding: 3px 5px 2px 5px;\r\n	text-shadow: #333333 1px 1px;\r\n	border: 1px solid #333333;\r\n	cursor: pointer;\r\n	-moz-border-radius: 2px;-khtml-border-radius: 2px;-webkit-border-radius: 2px;\r\n}\r\n.submit:hover\r\n{\r\n	background: url(\'styles/{themename}/theme_images/navigation-hover.png\') center;\r\n	text-decoration: none;\r\n	border: 1px solid #e47600;\r\n}\r\n\r\n/* CSS3 Focus Effects for Input and Textarea */\r\ninput, textarea\r\n{\r\n  -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);\r\n  -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);\r\n  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);\r\n  -webkit-transition: border linear 0.2s, box-shadow linear 0.2s;\r\n  -moz-transition: border linear 0.2s, box-shadow linear 0.2s;\r\n  -ms-transition: border linear 0.2s, box-shadow linear 0.2s;\r\n  -o-transition: border linear 0.2s, box-shadow linear 0.2s;\r\n  transition: border linear 0.2s, box-shadow linear 0.2s;\r\n}\r\n\r\ninput:focus, textarea:focus\r\n{\r\n  border-color: rgba(82, 168, 236, 0.8);\r\n  -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6);\r\n  -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6);\r\n  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6);\r\n  outline: 0;\r\n  outline: thin dotted \\9;\r\n  /* IE6-9 */\r\n}\r\n\r\ninput[type=\"file\"]:focus,\r\ninput[type=\"radio\"]:focus,\r\ninput[type=\"checkbox\"]:focus,\r\nselect:focus\r\n{\r\n  -webkit-box-shadow: none;\r\n  -moz-box-shadow: none;\r\n  box-shadow: none;\r\n  outline: thin dotted #333;\r\n  outline: 5px auto -webkit-focus-ring-color;\r\n  outline-offset: -2px;\r\n}\r\n\r\n/***** OVERLAY *********************/\r\n#overlay\r\n{\r\n	display:none;\r\n	z-index:10000;\r\n	min-width: 600px;\r\n	-