(25, 1, 'tinymce_dialog_image', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n	\r\n	<head>\r\n		<title>{$Page_Title}</title>\r\n		<meta http-equiv=\"Content-Type\" content=\"text/html; charset={$TSUE[\'TSUE_Language\']->charset}\" />\r\n\r\n		<link rel=\"stylesheet\" href=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/style.php?l=style,fancybox,image_gallery\" type=\"text/css\" media=\"screen\" />\r\n\r\n		<script type=\"text/javascript\">\r\n			var TSUEPhrases = [];\r\n		</script>\r\n		<script type=\"text/javascript\" src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/js/tsue/jquery.js\"></script>\r\n		\r\n		<script type=\"text/javascript\" src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/js/tiny_mce/tiny_mce_popup.js\"></script>\r\n		<script type=\"text/javascript\" src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/js/tiny_mce/utils/form_utils.js\"></script>\r\n		<script type=\"text/javascript\" src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/js/tiny_mce/themes/tsue/js/image.js\"></script>\r\n\r\n		<style type=\"text/css\">\r\n			html,body{overflow-x: hidden;}\r\n		</style>\r\n		\r\n	</head>\r\n	\r\n	<body id=\"image\" style=\"display: none\">\r\n		\r\n		<div id=\"insertEditImageForm\">\r\n			<form onsubmit=\"ImageDialog.update(); return false;\" method=\"post\">\r\n				<div>\r\n					\r\n					<div class=\"tableHeader\">\r\n						<div class=\"row\">\r\n							<div class=\"cell first\">\r\n								{$Page_Title}\r\n							</div>\r\n						</div>\r\n					</div>\r\n\r\n					<div class=\"table\">\r\n						<div class=\"row\">\r\n							<div class=\"cell\">\r\n								<input name=\"src\" type=\"text\" class=\"s\" placeholder=\"{$TSUE[\'TSUE_Language\']->phrase[\'enter_image_url_here\']}\" style=\"width: 90%\" /> \r\n								<input type=\"submit\" name=\"insert\" value=\"{$TSUE[\'TSUE_Language\']->phrase[\'button_insert\']}\" class=\"submit\" />\r\n							</div>\r\n						</div>\r\n					</div>\r\n					\r\n				</div>\r\n			</form>\r\n		</div>\r\n\r\n		<div style=\"text-align: center; margin-top: 11px;\" id=\"popupLinks\">\r\n			<span id=\"popupImageGallery\">\r\n				<span class=\"clickable\">{$TSUE[\'TSUE_Language\']->phrase[\'image_gallery\']}</span>\r\n			</span>			\r\n\r\n			<span style=\"margin-left: 5px;\" id=\"uploadAFile\">\r\n				| <span class=\"clickable\">{$TSUE[\'TSUE_Language\']->phrase[\'dm_upload_a_file\']}</span>\r\n			</span>\r\n		</div>\r\n\r\n		{$Error}\r\n\r\n		<script type=\"text/javascript\">\r\n			{$jsUploadedVar}\r\n\r\n			jQuery(document).ready(function()\r\n			{\r\n				function showGalleryImages()\r\n				{\r\n					jQuery(\'#show_error\').remove();\r\n\r\n					jQuery(\'#popupLinks\').html(\'<img src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/styles/{$TSUE[\'TSUE_Template\']->ThemeName}/ajax/ajax_loading.gif\" alt=\"\" title=\"\" class=\"middle\" /> {$TSUE[\'TSUE_Language\']->phrase[\'loading\']}\');\r\n\r\n					jQuery.ajax\r\n					({\r\n						type: \'POST\',\r\n						url: \'{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/ajax/gallery.php\',\r\n						dataType: \'html\',\r\n						contentType: \'application/x-www-form-urlencoded; charset={$TSUE[\'TSUE_Language\']->charset}\',\r\n						encoding: \'{$TSUE[\'TSUE_Language\']->charset}\',\r\n						cache: false,\r\n						timeout: 25000,\r\n						data: \'action=galleryImagesForTinyMCE&securitytoken={$TSUE[\'TSUE_Member\']->info[\'csrf_token_page\']}\',\r\n						success: function(response)\r\n						{\r\n							jQuery(response).insertBefore(jQuery(\'#popupLinks\'));\r\n							jQuery(\'#popupLinks\').remove();\r\n							jQuery(document).on(\'click\', \'div[rel=\"galleryImages\"]\', function(e)\r\n							{\r\n								jQuery(document).find(\'div[rel=\"galleryImages\"]\').css({\'border\':\'1px solid #ccc\'});\r\n								jQuery(\'input[name=\"src\"]\').val(jQuery(this).find(\'img\').attr(\'src\').replace(/\\/data\\/gallery\\/s\\//, \'/data/gallery/l/\')).focus();\r\n								jQuery(this).css({\'border\':\'1px solid red\'});\r\n							});\r\n							jQuery(\'a.fancybox\').fancybox();\r\n						}\r\n					});\r\n				}\r\n\r\n				if(isUploaded)\r\n				{\r\n					showGalleryImages();\r\n				}\r\n\r\n				jQuery(\'#popupImageGallery\').click(function(e)\r\n				{\r\n					e.preventDefault();\r\n\r\n					showGalleryImages();\r\n					\r\n					return false;\r\n				});\r\n\r\n				jQuery(\'#uploadAFile\').click(function(e)\r\n				{\r\n					e.preventDefault();\r\n\r\n					jQuery(\'#show_error\').remove();\r\n\r\n					jQuery(\'#popupLinks\').html(\'<img src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/styles/{$TSUE[\'TSUE_Template\']->ThemeName}/ajax/ajax_loading.gif\" alt=\"\" title=\"\" class=\"middle\" /> {$TSUE[\'TSUE_Language\']->phrase[\'loading\']}\');\r\n\r\n					jQuery.ajax\r\n					({\r\n						type: \'POST\',\r\n						url: \'{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/ajax/gallery.php\',\r\n						dataType: \'html\',\r\n						contentType: \'application/x-www-form-urlencoded; charset={$TSUE[\'TSUE_Language\']->charset}\',\r\n						encoding: \'{$TSUE[\'TSUE_Language\']->charset}\',\r\n						cache: false,\r\n						timeout: 25000,\r\n						data: \'action=galleryImageUploadForTinyMCE&securitytoken={$TSUE[\'TSUE_Member\']->info[\'csrf_token_page\']}\',\r\n						success: function(response)\r\n						{\r\n							jQuery(response).insertBefore(jQuery(\'#popupLinks\'));\r\n							jQuery(\'#popupLinks\').remove();\r\n							jQuery(\'#ig_upload_form\').submit(function(e)\r\n							{\r\n								jQuery(\'#igUploadForm\').fadeOut(\'fast\', function(){jQuery(\'#igUploading\').fadeIn(\'fast\');});\r\n							});\r\n						}\r\n					});\r\n					\r\n					return false;\r\n				});\r\n			});\r\n		</script>\r\n\r\n	</body>\r\n</html>', 1404823543, ),
