(5643, 1, 'top_donors', '<div class=\"widget\">\r\n	<h4>\r\n		<span class=\"floatright\">\r\n			<img src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/styles/{$TSUE[\'TSUE_Template\']->ThemeName}/buttons/refresh.png\" alt=\"{$TSUE[\'TSUE_Language\']->phrase[\'button_refresh\']}\" title=\"{$TSUE[\'TSUE_Language\']->phrase[\'button_refresh\']}\" rel=\"refreshTopDonors\" class=\"clickable middle\" />\r\n		</span>\r\n		<img src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/styles/{$TSUE[\'TSUE_Template\']->ThemeName}/forums/mix/{$toggleSrc}.png\" alt=\"\" title=\"\" rel=\"top_donors_list\" id=\"toggle\" class=\"middle pointer\" /> {$TSUE[\'TSUE_Language\']->phrase[\'top_donors\']}\r\n	</h4>\r\n	<div id=\"top_donors_list\" class=\"{$class}\">{$top_donors_list}</div>\r\n	<div class=\"clear\"></div>\r\n</div>', 1404823543, ),
