(6142, 1, 'TSUEPlugin_Radio', '<script type=\"text/javascript\" src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/js/radio/swfobject.js\"></script>\r\n\r\n<div class=\"widget\">\r\n	<h4>\r\n		<img src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/styles/{$TSUE[\'TSUE_Template\']->ThemeName}/forums/mix/{$toggleSrc}.png\" alt=\"\" title=\"\" rel=\"TSUERadio\" id=\"toggle\" class=\"middle pointer\" /> \r\n		{$radioOptions[\'radio_swfradiochannel\']}\r\n	</h4>\r\n	<p id=\"TSUERadio\" class=\"{$class}\">		\r\n		<strong>Sorry this site have a flash based native radio and needed adobe flash 10+ support. </strong><br />\r\n		<a href=\"http://www.adobe.com/shockwave/download/download.cgi?P1_Prod_Version=ShockwaveFlash\">Download Flash here.</a>\r\n	</p>\r\n</div>\r\n\r\n<script type=\"text/javascript\">\r\n// <![CDATA[	\r\n	var so = new SWFObject(\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/js/radio/tsueRadio.swf\", \"tsueRadio\", \"{$radioOptions[\'radio_width\']}\", \"{$radioOptions[\'radio_height\']}\", \"{$radioOptions[\'radio_version\']}\", \"{$radioOptions[\'radio_bg\']}\");\r\n	so.addVariable(\"swfcolor\", \"{$radioOptions[\'radio_swfcolor\']}\");\r\n	so.addVariable(\"swfwidth\", \"{$radioOptions[\'radio_width\']}\");\r\n	so.addVariable(\"swfradiochannel\", \"{$radioOptions[\'radio_swfradiochannel\']}\");\r\n	so.addVariable(\"swfstreamurl\", \"{$radioOptions[\'radio_swfstreamurl\']}\");\r\n	so.addVariable(\"swfinformations\", \"{$radioOptions[\'radio_swfinformations\']}\");\r\n	so.addVariable(\"swfpause\", \"1\");\r\n	so.write(\"TSUERadio\");	\r\n// ]]>\r\n</script>', 1404823543, ),
(6143, 1, 'TSUEPlugin_ads', '<div class=\"widget\" id=\"advertisements\">\r\n	<h4>\r\n		<img src=\"{$TSUE[\'TSUE_Settings\']->settings[\'global_settings\'][\'website_url\']}/styles/{$TSUE[\'TSUE_Template\']->ThemeName}/forums/mix/{$toggleSrc}.png\" alt=\"\" title=\"\" rel=\"showAds\" id=\"toggle\" class=\"middle pointer\" /> \r\n		<span rel=\"adsTitle\">{$adsTitle}</span>\r\n	</h4>\r\n	\r\n	<div id=\"showAds\" class=\"{$class}\">{$TSUEPlugin_ads_rows}</div>\r\n</div>\r\n\r\n<script type=\"text/javascript\">\r\n	var totalAds = {$totalAds}, rotate, currentAd = 1, nextAd = 2;\r\n\r\n	function rotateAds()\r\n	{\r\n		if(nextAd > totalAds)\r\n		{\r\n			nextAd = 1;\r\n		}\r\n		\r\n		$(\'div[data-adNumber=\"\'+currentAd+\'\"]\').fadeOut(\"slow\", function()\r\n		{\r\n			$(\'span[rel=\"adsTitle\"]\').html($(\'div[data-adNumber=\"\'+nextAd+\'\"] .adsTitle\').html());\r\n\r\n			$(\'div[data-adNumber=\"\'+nextAd+\'\"]\').fadeIn(\"slow\", function()\r\n			{				\r\n				currentAd = nextAd;\r\n				nextAd++;\r\n			});\r\n		});\r\n	}\r\n\r\n	window.onload=function()\r\n	{\r\n		rotate = setInterval(function(){rotateAds()}, 5000);\r\n\r\n		$(\"#advertisements\").mouseover(function()\r\n		{\r\n			clearInterval(rotate);\r\n		}).mouseout(function()\r\n		{\r\n			rotate = setInterval(function(){rotateAds()}, 5000);\r\n		});\r\n	};\r\n</script>', 1404823543, ),
